/***************************************************************************************************
 * Class implementation of a result. Its job is to store information related to a single result, *
 * it is generated by the browsers. *
***************************************************************************************************/
#include <string>
#include <sstream>

class Result
{
private:
    int line;
    std::string prev;
    std::string next;
    std::string match;
    std::string path;
    int start;
    int end;

public:
    Result(int line, std::string prev, std::string next, std::string match, std::string path, int start, int end)
    {
        this->line = line;
        this->prev = prev;
        this->next = next;
        this->match = match;
        this->path = path;
        this->start = start;
        this->end = end;
    }

    std::string get_prev()
    {
        return prev;
    }

    std::string get_next()
    {
        return next;
    }

    int get_line()
    {
        return line;
    }

    std::string get_match()
    {
        return match;
    }

    std::string get_path()
    {
        return path;
    }

    int get_start()
    {
        return start;
    }

    int get_end()
    {
        return end;
    }

    operator std::string() const
    {
        std::ostringstream oss;
        // this->line+1 just for formatting porpuses
        oss << "Start: " << this->start << " - end: " << this->end << "] :: line " << this->line + 1 << " :: ... " << this->prev << " " << this->match << " " << this->next << " ...";
        return oss.str();
    }
};